import React, { useState } from "react";
import "./createpost.css";

const CreatePost = () => {
    const [postText, setPostText] = useState("");

    const handlePost = () => {
        if (postText.trim()) {
            alert(`Your post: ${postText}`);
            setPostText(""); // Clear the input after posting
        }
    };

    return (
        <div className="create-post-container">
            <div className="create-post-header">
                <h3>Create Post</h3>
                <div className="privacy-select">
                    <i className="globe-icon"></i>
                    <select>
                        <option value="anyone">Anyone</option>
                        <option value="connections">Connections</option>
                    </select>
                </div>
            </div>
            <textarea
                placeholder="What do you talk about?"
                value={postText}
                onChange={(e) => setPostText(e.target.value)}
            />
            <div className="create-post-footer">
                <div className="post-options">
                    <button className="post-option">
                        <i className="icon-photos"></i> Photos
                    </button>
                    <button className="post-option">
                        <i className="icon-videos"></i> Videos
                    </button>
                    <button className="post-option">
                        <i className="icon-documents"></i> Documents
                    </button>
                    <button className="post-option">
                        <i className="icon-event"></i> Event
                    </button>
                </div>
                <button className="post-btn" onClick={handlePost}>
                    POST
                </button>
            </div>
        </div>
    );
};

export default CreatePost;
