import React, { useState } from "react";
import './Sidebar.css';
import '@fortawesome/fontawesome-free/css/all.min.css';


  // Import Font Awesome CSS

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`sidebar ${isOpen ? 'open' : ''}`}>
      {/* Profile Section */}
      <div className="profile-section">
        <img
          src="https://via.placeholder.com/100" // Replace with your image URL
          alt="Profile"
          className="profile-photo"
        />
        <div className="profile-details">
          <h2 className="profile-title">John Doe</h2>
          <p className="profile-description">Web Developer</p>
          <p className="profile-followers">
            <i className="fa fa-user-plus"></i> 1,234
          </p>
        </div>
      </div>

      {/* Toggle Button for smaller screens */}
      <button className="toggle-btn" onClick={toggleSidebar}>
        <i className={`fa ${isOpen ? 'fa-times' : 'fa-bars'}`}></i>
      </button>

      {/* Navigation Section */}
      <hr className="profile-hr" />
      <div className="navigation">
        <ul>
          <li>
            <i className="fa fa-envelope"></i> Create Email
          </li>
          <li>
            <i className="fa fa-history"></i> Activity Log
          </li>
          <li>
            <i className="fa fa-calendar-check"></i> Scheduled
          </li>
          <li>
            <i className="fa fa-inbox"></i> Inbox
          </li>
          <li>
            <i className="fa fa-database"></i> Infobase
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
