import React, { useState } from "react";
import url from 'en'
const Homeinvestor = () => {
  const [formData, setFormData] = useState({
    sector: "",
    investor: "",
    state: "",
    city: "",
  });

  // State-to-city mapping
  const stateCityMapping = {
    Maharashtra: ["Mumbai", "Pune", "Nagpur"],
    Karnataka: ["Bengaluru", "Mysuru", "Mangaluru"],
    Gujarat: ["Ahmedabad", "Surat", "Vadodara"],
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
      // Reset city if the state changes
      city: name === "state" ? "" : prevState.city,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { sector, investor } = formData;

    // Ensure required fields are provided
    if (!sector || !investor) {
      alert("Sector and Investor are required fields.");
      return;
    }

    try {
      const response = await fetch("investor/selectinvestor", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        alert("Investor added successfully!");
        setFormData({ sector: "", investor: "", state: "", city: "" }); // Reset form
      } else {
        alert(`Error: ${data.error}`);
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("An error occurred while adding the investor.");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "auto" }}>
      <h2>Add Investor</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="sector">Sector</label>
        <select
          id="sector"
          name="sector"
          value={formData.sector}
          onChange={handleChange}
        >
          <option value="">Select Sector</option>
          <option value="Technology">Technology</option>
          <option value="Finance">Finance</option>
          <option value="Healthcare">Healthcare</option>
        </select>

        <label htmlFor="investor">Investor</label>
        <input
          type="text"
          id="investor"
          name="investor"
          placeholder="Enter investor name"
          value={formData.investor}
          onChange={handleChange}
        />

        <label htmlFor="state">State</label>
        <select
          id="state"
          name="state"
          value={formData.state}
          onChange={handleChange}
        >
          <option value="">Select State</option>
          {Object.keys(stateCityMapping).map((state) => (
            <option key={state} value={state}>
              {state}
            </option>
          ))}
        </select>

        <label htmlFor="city">City</label>
        <select
          id="city"
          name="city"
          value={formData.city}
          onChange={handleChange}
          disabled={!formData.state} // Disable city dropdown if no state is selected
        >
          <option value="">Select City</option>
          {formData.state &&
            stateCityMapping[formData.state].map((city) => (
              <option key={city} value={city}>
                {city}
              </option>
            ))}
        </select>

        <button type="submit" style={{ backgroundColor: "#28a745", color: "#fff" }}>
          Add Investor
        </button>
      </form>
    </div>
  );
};

export default Homeinvestor;
