import React, { useState } from "react";

const Homeinvestor = () => {
  const [formData, setFormData] = useState({
    sector: "",
    investor: "",
    state: "",
    city: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { sector, investor } = formData;

    // Ensure required fields are provided
    if (!sector || !investor) {
      alert("Sector and Investor are required fields.");
      return;
    }

    try {
      const response = await fetch("http://localhost:8090/investor/selectinvestor", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        alert("Investor added successfully!");
        setFormData({ sector: "", investor: "", state: "", city: "" }); // Reset form
      } else {
        alert(`Error: ${data.error}`);
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("An error occurred while adding the investor.");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "auto" }}>
      <h2>Add Investor</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="sector">Sector</label>
        <select
          id="sector"
          name="sector"
          value={formData.sector}
          onChange={handleChange}
        >
          <option value="">Select Sector</option>
          <option value="Technology">Technology</option>
          <option value="Finance">Finance</option>
          <option value="Healthcare">Healthcare</option>
        </select>

        <label htmlFor="investor">Investor</label>
        <input
          type="text"
          id="investor"
          name="investor"
          placeholder="Enter investor name"
          value={formData.investor}
          onChange={handleChange}
        />

        <label htmlFor="state">State</label>
        <select
          id="state"
          name="state"
          value={formData.state}
          onChange={handleChange}
        >
          <option value="">Select State</option>
          <option value="California">California</option>
          <option value="New York">New York</option>
          <option value="Texas">Texas</option>
        </select>

        <label htmlFor="city">City</label>
        <select
          id="city"
          name="city"
          value={formData.city}
          onChange={handleChange}
        >
          <option value="">Select City</option>
          <option value="San Francisco">San Francisco</option>
          <option value="New York City">New York City</option>
          <option value="Houston">Houston</option>
        </select>

        <button type="submit" style={{ backgroundColor: "#28a745", color: "#fff" }}>
          Add Investor
        </button>
      </form>
    </div>
  );
};

export default Homeinvestor;
